    get original(): string;
    /**
     * @returns a string that has been built up from the text of its consituent Chars
     *
     * ```typescript
     * const text: Text = new Text("????????????????");
     * const clusters = text.clusters.map((cluster) => cluster.text);
     * // [
     * //  "????",
     * //  "????",
     * //  "????",
     * //  "??",
     * //  "??"
     * // ]
     * ```
     */
    get text(): string;
